import React, { useContext } from "react";
import { useHistory } from "react-router-dom";
import { Form, Input, Button, Row, Col, Spin } from "antd";
import { LoadingOutlined } from '@ant-design/icons' ;
import { logoutFromFirebase, updateUserInfo, getOrder } from "../actions";
import { StoreContext } from "../store";

const ProfileCard = () => {
  const {
    state: {
      userSignin: { userInfo }, checkUserOrder: {order, loading, error,}
    },
    dispatch,
  } = useContext(StoreContext);
  const { displayName, email } = userInfo;
  const history = useHistory();
  const [form] = Form.useForm();
  const antIcon = <LoadingOutlined style={{ fontSize: 80, color: "#8183ff" }} spin />;

  const handleUpdate = (values) => {
    console.log(values)
    updateUserInfo(dispatch, values);
  };

  const handleLogout = () => {
    logoutFromFirebase(dispatch);
    history.push("/");
  };

  const handleCheckOrders = () => {
    console.log(order);
    getOrder(dispatch);
  };

  return (
    <div className="userProfile">
    <Form
      onFinish={handleUpdate}
      name="normal_login"
      className="login-form"
      form={form}
      initialValues={userInfo}
    >
      <Form.Item
        label="name: "
        name="name"
        rules={[
          {
            type: "string",
            message: "The input is not valid name!",
          },
          {
            message: "Please input your name!",
          },
        ]}
        hasFeedback
      >
        <Input placeholder={displayName} />
      </Form.Item>
      <Form.Item
        label="email: "
        name="email"
        rules={[
          {
            type: "email",
            message: "The input is not valid E-mail!",
          },
          {
            message: "Please input your E-mail!",
          },
        ]}
        hasFeedback
      >
        <Input placeholder={email} />
      </Form.Item>
      <Form.Item
        name="password"
        label="Password"
        rules={[
          {
            message: "Please input your password!",
          },
        ]}
        hasFeedback
      >
        <Input.Password />
      </Form.Item>

      <Form.Item
        name="rePassword"
        label="Re-enter Password"
        dependencies={["password"]}
        hasFeedback
        rules={[
          {
            message: "Please re-enter your password!",
          },
          ({ getFieldValue }) => ({
            validator(_, value) {
              if (!value || getFieldValue("password") === value) {
                return Promise.resolve();
              }

              return Promise.reject(
                new Error("The two passwords that you entered do not match!")
              );
            },
          }),
        ]}
      >
        <Input.Password />
      </Form.Item>
      
      <Form.Item>
        <Button
          type="primary"
          htmlType="submit"
          style={{ marginTop: "0.8rem" }}
          className="login-form__button"
        >
          Submit
        </Button>

        <Button
          type="danger"
          style={{ marginTop: "0.8rem" }}
          className="login-form__button"
          onClick={handleLogout}
        >
          Log out
        </Button>
        
      </Form.Item>
    </Form>

    <Button
      type="Info"
      className="login-form__button"
      onClick={handleCheckOrders}
    >
      Order
    </Button>

    <div className="userOrder">
    {loading
            ? (
               <div className="spinner-wrap">
                  <Spin indicator={antIcon} className="spinner" />
               </div>
            ) : (
               <Row gutter={[24, 24]}>
                  <Col
                     xs={{ span: 20, offset: 2 }}
                  >
                     <div className="card card-body">
                        <h2 style={{ color: 'white' }}>Shipping</h2>
                        <p>
                           <strong>Name:</strong> {order.shippingAddress.fullName} <br />
                           <strong>Address: </strong> {order.shippingAddress.address},
                  {order.shippingAddress.city}, {order.shippingAddress.postalCode}
                  ,{order.shippingAddress.country}
                        </p>
                     </div>
                     <div className="card card-body">
                        <h2 style={{ color: 'white' }}>Payment</h2>
                        <p>
                           <strong>Method:</strong> {order.paymentMethod}
                        </p>
                     </div>
                     <div className="card card-body">
                        <h2 style={{ color: 'white' }}>Order Items</h2>
                        {   orderItems.map(item => (
                              <li key={item.id} className="cart-item">
                                 <div className="cart-image">
                                    <img src={item.image} alt={item.name} />
                                 </div>
                                 <div className="cart-item-content">
                                    <div className="cart-name">{item.name}</div>
                                    <div className="product-qty">
                                       Qty: {item.qty}
                                    </div>
                                 </div>
                                 <div className="cart-item-end">
                                    <div className="cart-price">
                                       ${item.price * item.qty}
                                    </div>
                                 </div>

                              </li>
                           ))
                        }
                        <div className="cart-total-price-wrap">
                           Total
            <div className="cart-total-price">${order.totalPrice}</div>
                        </div>
                     </div>

                  </Col>
                  <Col
                     xs={{ span: 20, offset: 2 }}
                     lg={{ span: 7, offset: 0 }}
                  >
                     <div className="card card-body">
                        <h2 style={{ color: 'white' }}>Order Summary</h2>
                        <div className="row">
                           <div>Items</div>
                           <div>${order.itemsPrice}</div>
                        </div>
                        <div className="row">
                           <div>Shipping</div>
                           <div>${order.shippingPrice}</div>
                        </div>
                        <div className="row">
                           <div>Tax</div>
                           <div>${order.taxPrice}</div>
                        </div>
                        <div className="row">
                           <div>
                              <strong> Order Total</strong>
                           </div>
                           <div>
                              <strong>${order.totalPrice}</strong>
                           </div>
                        </div>
                     </div>

                  </Col>
               </Row>

            )

         }
    </div>
    </div>
  );
};
export default ProfileCard;
